jsonContent = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
inputStream.close()

// Parse the JSON content
def jsonSlurper = new JsonSlurper()
def records = jsonSlurper.parseText(jsonContent)

// Add sequential 'group' to each record, starting from g1
int idCounter = 1
records.each { record ->
    record['group'] = "g" + idCounter
    idCounter++
}

// Wrap the content in the required structure with 'client' and 'user'
