properties.jslt-transform-transformation:
let nextLink = [for (.response.links)(.href) if (.rel == "next") ]
{
  "method": .method,
  "path": split($nextLink[0],"/services")[1],
  "headers": {
    "Prefer": "transient"
  },
  "data": {
    "q": "SELECT S.name AS subsidiaryname, TAL.account, acc.acctnumber, SUM(TAL.DEBIT) AS CLOSING_DEBIT, SUM(TAL.CREDIT) AS CLOSING_CREDIT, NVL(TRNX.TRNX_DR, 0) AS TRNX_DR, NVL(TRNX.TRNX_CR, 0) AS TRNX_CR, SUM(TAL.DEBIT) - NVL(TRNX.TRNX_DR, 0) AS OP_DR, SUM(TAL.CREDIT) - NVL(TRNX.TRNX_CR, 0) AS OP_CR FROM transaction TR INNER JOIN transactionLine TL ON TR.ID = TL.transaction INNER JOIN TransactionAccountingLine TAL ON TL.ID = TAL.transactionline AND TAL.transaction = TL.transaction LEFT JOIN account ACC ON TAL.account = ACC.ID LEFT JOIN subsidiary S ON TL.subsidiary = S.id LEFT JOIN (SELECT TL.subsidiary AS TRNX_SUBSIDIARY, TAL.account AS TRNX_ACCOUNT, SUM(TAL.DEBIT) AS TRNX_DR, SUM(TAL.CREDIT) AS TRNX_CR FROM transaction TR INNER JOIN transactionLine TL ON TR.ID = TL.transaction INNER JOIN TransactionAccountingLine TAL ON TL.ID = TAL.transactionline AND TAL.transaction = TL.transaction LEFT JOIN account ACC ON TAL.account = ACC.ID WHERE TR.trandate >= '06/01/2025' AND TR.trandate <= '06/30/2025' AND TAL.amount IS NOT NULL AND TR.posting = 'T' GROUP BY TL.subsidiary, TAL.account) TRNX ON TRNX.TRNX_SUBSIDIARY = TL.subsidiary AND TRNX.TRNX_ACCOUNT = TAL.account WHERE TR.trandate <= '06/30/2025' AND TAL.amount IS NOT NULL AND TR.posting = 'T' GROUP BY S.name, TAL.account, acc.acctnumber, TRNX.TRNX_DR, TRNX.TRNX_CR ORDER BY S.name, acc.acctnumber;"
  },
  "request_format": "JSON"
}

