import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.*

// Read the incoming flow file content (assumed to be a JSON object)
flowFile = session.get()
if (!flowFile) return

inputStream = session.read(flowFile)
jsonContent = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
inputStream.close()

// Parse the JSON content
def jsonSlurper = new JsonSlurper()
def inputData = jsonSlurper.parseText(jsonContent)

// Initialize a list to hold the flattened data
def flattenedData = []

// Function to check and replace null values with 0
def safeValue(value) {
    return value ?: 0
}

// Ensure 'results' exists and is an array
if (inputData?.results instanceof List) {
    // Iterate over each parent object in the results array
    inputData.results.each { parent ->
        // Ensure 'general_ledger_transaction_lines' exists and is an array
        if (parent.general_ledger_transaction_lines instanceof List) {
            parent.general_ledger_transaction_lines.each { line ->
                // Flatten the required fields with type checks
                def flatRecord = [
                    com_id: line.id,
                    glcode: parent.company,
                    date: parent.posting_date,
                    debit: safeValue(line.debit_amount), // Only debit amount
                    credit: safeValue(line.credit_amount), // Only credit amount
                    opening_amount: 0, // Static value
                    audit_entries: 0,  // Static value
                    stage: 0,          // Static value
                    status: true,      // Static value
                    base_currency: line.base_currency,
                    f_debit: safeValue(line.foreign_debit_amount), // Safe value for foreign debit
                    f_credit: safeValue(line.foreign_credit_amount), // Safe value for foreign credit
                    f_currency: line.transaction_currency
                ]
                flattenedData.add(flatRecord)
            }
        }
    }
}

// Wrap the content in the required structure with 'client' and 'user'
def result = flattenedData

// Convert back to JSON string
def jsonBuilder = new JsonBuilder(result)
def outputContent = jsonBuilder.toPrettyString()

// Write the modified JSON content back to the flow file
flowFile = session.write(flowFile, { outputStream ->
    outputStream.write(outputContent.getBytes(StandardCharsets.UTF_8))
} as OutputStreamCallback)

session.transfer(flowFile, REL_SUCCESS)
